from dataclasses import dataclass

STATES = ["Healthy", "Stable", "Critical"]
ACTIONS = ["NoTreatment", "Standard", "Aggressive"]

S = {name: i for i, name in enumerate(STATES)}
A = {name: i for i, name in enumerate(ACTIONS)}

@dataclass
class StepResult:
    next_state: int
    reward: float
    done: bool

class SimpleMedicalEnv:
    def __init__(self, max_steps=20):
        self.max_steps = max_steps
        self.cost = {
            A["NoTreatment"]: 0.0,
            A["Standard"]: 0.5,
            A["Aggressive"]: 1.5,
        }
        self.reset()

    def reset(self, start_state="Critical"):
        self.state = S[start_state]
        self.t = 0
        return self.state

    def step(self, action):
        self.t += 1
        s = self.state

        if s == S["Critical"]:
            if action == A["Aggressive"]:
                probs = [(S["Stable"], 0.7), (S["Critical"], 0.25), (S["Healthy"], 0.05)]
                base_reward = 8
            elif action == A["Standard"]:
                probs = [(S["Stable"], 0.45), (S["Critical"], 0.45), (S["Healthy"], 0.10)]
                base_reward = 4
            else:
                probs = [(S["Critical"], 0.7)]
                base_reward = -6

        elif s == S["Stable"]:
            if action == A["Aggressive"]:
                probs = [(S["Healthy"], 0.65), (S["Stable"], 0.3), (S["Critical"], 0.05)]
                base_reward = 4
            elif action == A["Standard"]:
                probs = [(S["Healthy"], 0.5), (S["Stable"], 0.45), (S["Critical"], 0.05)]
                base_reward = 2
            else:
                probs = [(S["Stable"], 0.6), (S["Healthy"], 0.35)]
                base_reward = 0.5

        else:  # Healthy
            if action == A["Aggressive"]:
                probs = [(S["Healthy"], 0.9), (S["Stable"], 0.08), (S["Critical"], 0.02)]
                base_reward = -2
            elif action == A["Standard"]:
                probs = [(S["Healthy"], 0.93), (S["Stable"], 0.06)]
                base_reward = -0.5
            else:
                probs = [(S["Healthy"], 0.95)]
                base_reward = 1

        r = np.random.rand()
        cum = 0
        next_state = s
        for ns, p in probs:
            cum += p
            if r <= cum:
                next_state = ns
                break

        bonus = 2 if next_state < s else (-3 if next_state > s else 0)
        reward = base_reward + bonus - self.cost[action]

        self.state = next_state
        done = self.t >= self.max_steps
        return StepResult(next_state, reward, done)

env = SimpleMedicalEnv()

def epsilon_greedy(Q, s, eps):
    if np.random.rand() < eps:
        return np.random.randint(Q.shape[1])
    return np.argmax(Q[s])

def q_learning(env, episodes=2000, alpha=0.1, gamma=0.95, epsilon=0.3):
    Q = np.zeros((len(STATES), len(ACTIONS)))
    rewards = []

    for ep in range(episodes):
        s = env.reset()
        total = 0
        while True:
            a = epsilon_greedy(Q, s, epsilon)
            res = env.step(a)
            Q[s, a] += alpha * (res.reward + gamma * np.max(Q[res.next_state]) - Q[s, a])
            total += res.reward
            s = res.next_state
            if res.done:
                break
        rewards.append(total)

    return Q, rewards

Q, rewards = q_learning(env)

